sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

 var responseBody = {};
    var requestBody = request.body.data;

    function getUserSysId(username) {
        if (!username) return "";
        var usr = new GlideRecord("sys_user");
        usr.addQuery("user_name", username.trim());
        usr.query();
        if (usr.next()) {
            return usr.sys_id.toString();
        }
        return "";
    }

    function getUserSysIdsFromUsernames(usernames) {
        if (!usernames) return "";
        var sysIds = [];
        var userList = usernames.split(",");
        for (var i = 0; i < userList.length; i++) {
            var id = getUserSysId(userList[i]);
            if (id) sysIds.push(id);
        }
        return sysIds.join(",");
    }

    // Validate mandatory field
    if (!requestBody.user_entered) {
        response.setStatus(400);
        return {
            status: "ERROR",
            message: "Please include all mandatory fields (user_entered)."
        };
    }

    try {
        // sys_id of your Record Producer
        var recordProducerSysId = "8ad978322bebaa50da0ef1bece91bf17";

        // Build variables object
        var variables = {
            "budget_adj_nbr": requestBody.budget_adj_nbr,
            "admin_unit_name": requestBody.admin_unit_name,
            "fiscal_year": requestBody.fiscal_year,
            "descr": requestBody.descr,
            "approval_status": requestBody.approval_status,
            "user": getUserSysId(requestBody.user_entered),   // converted to sys_id
            "internal_user": getUserSysId(requestBody.user_requested), // converted to sys_id
            "date_entered": requestBody.date_entered,
            "date_requested": requestBody.date_requested,
            "currency": requestBody.currency,
            "one_time_adj_flag": requestBody.one_time_adj_flag,
            "hdr_bu": requestBody.hdr_bu,
            "ref_number": requestBody.ref_number,
            "high_priority_flag": requestBody.high_priority_flag,
            "is_position_budget": requestBody.is_position_budget,
            "lvl1_approvers": getUserSysIdsFromUsernames(requestBody.approvers1_list),
            "lvl2_approvers": getUserSysIdsFromUsernames(requestBody.approvers2_list),
            "lvl3_approvers": getUserSysIdsFromUsernames(requestBody.approvers3_list),
            "tracking_num": requestBody.tracking_num
        };


        // Create catalog item API
        var catalogItem = new sn_sc.CatItem(recordProducerSysId);
        var values = {
            "variables": variables,
            "sysparm_id": recordProducerSysId
        };

        // Submit producer and get created record
        var targetRecordDetails = catalogItem.submitProducer(values);

        responseBody.status = "SUCCESS";
        responseBody.created_record = targetRecordDetails;

    } catch (ex) {
        response.setStatus(500);
        responseBody.status = "ERROR";
        responseBody.message = ex.message;
    }

    return responseBody;

})(request, response);
